CREATE TABLE Artist (\n    Artist_ID INT PRIMARY KEY,\n    Name VARCHAR(255) NOT NULL\n);\n\nCREATE TABLE Artist_Group (\n    Artist_Group_ID INT PRIMARY KEY AUTO_INCREMENT -- Use a surrogate key\n);\n\n-- This table enforces the many-to-many relationship and the uniqueness constraint\nCREATE TABLE Artist_Group_Participation (\n    Artist_Group_ID INT,\n    Artist_ID INT,\n    UNIQUE (Artist_Group_ID, Artist_ID),\n    FOREIGN KEY (Artist_Group_ID) REFERENCES Artist_Group(Artist_Group_ID),\n    FOREIGN KEY (Artist_ID) REFERENCES Artist(Artist_ID)\n);