set ANSI_NULLS ON\nset QUOTED_IDENTIFIER ON\nGO\nALTER PROCEDURE [dbo].[getSealRecordDate] (\n    @SEAL_DATE DATETIME\n)\nAS\nBEGIN\n    -- Declare a variable to hold the date plus one second\n    DECLARE @SEAL_DATE_PLUS_ONE_SEC DATETIME\n\n    -- Add one second to the seal date\n    SET @SEAL_DATE_PLUS_ONE_SEC = DATEADD(SECOND, 1, @SEAL_DATE)\n\n    -- Your query using the variables\n    SELECT DISTINCT \n           "FGFSTRANSFERS"."CONSIDERATION", \n           "FGFSTRANSFERS"."AMOUNT_TRANSFER", \n           "FGFSTRANSFERS"."DATE", \n           "FGFSTransferDetails"."TRANSFER_ID", \n           "FGFSTRANSFERS"."TRANSFER_ID", \n           "FGFSTRANSFERS"."Curr", \n           "FGFSTransferDetails"."CURR", \n           "FGFSCUSTOMERS"."CMF_NAME_1", \n           "FGFSCUSTOMERS"."CMF_NAME_2"\n    FROM ("FGFSInvestment"."dbo"."FGFSTransferDetails" "FGFSTransferDetails" \n          INNER JOIN "FGFSInvestment"."dbo"."FGFSTRANSFERS" "FGFSTRANSFERS" \n                   ON "FGFSTransferDetails"."TRANSFER_ID" = "FGFSTRANSFERS"."TRANSFER_ID") \n         INNER JOIN "FGFSInvestment"."dbo"."FGFSCUSTOMERS" "FGFSCUSTOMERS" \n                  ON "FGFSTRANSFERS"."CUSTODIAN" = "FGFSCUSTOMERS"."CMF_ACCOUNT"\n    WHERE ("FGFSTRANSFERS"."DATE" >= @SEAL_DATE AND "FGFSTRANSFERS"."DATE" < @SEAL_DATE_PLUS_ONE_SEC)\nEND