-- Create Trace\ndeclare @traceid int;\nexec sp_trace_create @traceid output, 2, N'C:\Path\ToDeleteAuditTrace';\n\n-- Add Events\ndeclare @on bit;\nset @on = 1;\n\nexec sp_trace_addnewevent @traceid, 45; -- SP:StmtCompleted\nexec sp_trace_addnewevent @traceid, 10; -- RPC:Completed\n\n-- Add Filters (to filter by specific keywords)\ndeclare @filter int;\nset @filter = 3;\n\nexec sp_trace_setfilter @traceid, @filter, 6, 7, N'%DELETE%';\nexec sp_trace_setfilter @traceid, @filter, 10, 0, N'TableX';\n\n-- Set the trace status to start\nexec sp_trace_setstatus @traceid, 1;\ngo