WITH MarketerData AS (\n    SELECT \n        Marketer,\n        SubMarketer,\n        Grade,\n        Total,\n        CONVERT(VARCHAR, Date, 101) AS [Date]\n    FROM SomeTable\n    WHERE Date >= '2/25/2009' AND Date < '2/26/2009'\n      AND Marketer IN ('22', '500')\n),\nMarketerTotals AS (\n    SELECT \n        Marketer,\n        SUM(Total) AS Total\n    FROM SomeTable\n    WHERE Date >= '2/25/2009' AND Date < '2/26/2009'\n      AND Marketer IN ('22', '500')\n    GROUP BY Marketer\n)\nSELECT \n    MD.Marketer,\n    MD.SubMarketer,\n    MD.Grade,\n    MD.Total,\n    MD.[Date]\nFROM MarketerData MD\n\nUNION ALL\n\nSELECT \n    MT.Marketer,\n    NULL AS SubMarketer,\n    NULL AS Grade,\n    MT.Total,\n    'Marketer Total' AS [Date] -- This can be adjusted to your preference for the label\nFROM MarketerTotals MT\n\nORDER BY \n    Marketer, \n    CASE WHEN SubMarketer IS NOT NULL THEN 0 ELSE 1 END, \n    SubMarketer;