USE YourDatabaseName;\nGO\n\nSELECT \n    PermissionTypes.state_desc, \n    PermissionTypes.permission_name,\n    Users.name AS UserName,\n    Objects.Name AS ObjectName\nFROM sys.database_permissions AS PermissionTypes\nJOIN sys.database_principals AS Users ON PermissionTypes.grantee_principal_id = Users.principal_id\nLEFT JOIN sys.objects AS Objects ON PermissionTypes.major_id = Objects.object_id;