-- Script to generate SQL statements for creating logins\nUSE master;\nGO\n\nSELECT 'CREATE LOGIN [' + NAME + '] WITH PASSWORD = 0x' + \n       CONVERT(varchar(MAX), password_hash, 2) + ' HASHED, SID = 0x' + \n       CONVERT(varchar(MAX), sid, 2) + ', CHECK_EXPIRATION = ' + \n       CASE is_expiration_checked WHEN 1 THEN 'ON' ELSE 'OFF' END + \n       ', CHECK_POLICY = ' + CASE is_policy_checked WHEN 1 THEN 'ON' ELSE 'OFF' END +\n       ';' AS CreateLogin\nFROM sys.sql_logins;\nGO\n\n-- Script to generate SQL statements for creating users\nUSE YourDatabaseName; -- Replace with your database name\nGO\n\nSELECT 'CREATE USER [' + NAME + '] FOR LOGIN [' + SUSER_SNAME(sid) + '];' AS CreateUser\nFROM sys.database_principals\nWHERE type_desc = 'SQL_USER';