-- Table for Root Posts Metadata\nCREATE TABLE root_posts_meta (\n    id INT AUTO_INCREMENT PRIMARY KEY,\n    creation_time DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,\n    last_modified TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,\n    views INT DEFAULT 0,\n    author_id INT, -- Assuming there is another table named 'users' with user info\n    FOREIGN KEY (author_id) REFERENCES users(id)\n);\n\n-- Table for Root Posts Data\nCREATE TABLE root_posts_data (\n    id INT PRIMARY KEY,\n    title VARCHAR(255),\n    body TEXT,\n    FULLTEXT(title, body),\n    FOREIGN KEY (id) REFERENCES root_posts_meta(id) ON DELETE CASCADE\n);\n\n-- Table for Sub-Posts Metadata\nCREATE TABLE sub_posts_meta (\n    id INT AUTO_INCREMENT PRIMARY KEY,\n    post_id INT, -- The parent post ID that this is a reply to\n    creation_time DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,\n    last_modified TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,\n    views INT DEFAULT 0,\n    author_id INT,\n    FOREIGN KEY (author_id) REFERENCES users(id),\n    FOREIGN KEY (post_id) REFERENCES root_posts_meta(id) ON DELETE CASCADE\n);\n\n-- Table for Sub-Posts Data\nCREATE TABLE sub_posts_data (\n    id INT PRIMARY KEY,\n    body TEXT,\n    FULLTEXT(body),\n    FOREIGN KEY (id) REFERENCES sub_posts_meta(id) ON DELETE CASCADE\n);