-- Create PseudoTempDB if it doesn't exist already\nIF NOT EXISTS (SELECT 1 FROM sys.databases WHERE name = 'PseudoTempDB')\nBEGIN\n    CREATE DATABASE PseudoTempDB;\nEND;\n\nUSE PseudoTempDB;\n\n-- Assuming your table creation script is stored as a block like this:\nDECLARE @tableCreationScript NVARCHAR(MAX) = N'\nCREATE TABLE RollupData (\n    -- Define your schema here\n);';\n\n-- If the table does not exist, create it\nIF OBJECT_ID('RollupData', 'U') IS NULL\nBEGIN\n    EXEC sp_executesql @tableCreationScript;\nEND;\n\n-- Insert data or whatever processing needs to be done\n-- ...\n\n-- Now, set up a scheduled job that periodically checks and deletes tables that have not been modified recently.\nDECLARE @deleteOldTables NVARCHAR(MAX) = N'\nUSE PseudoTempDB;\nDELETE FROM RollupData WHERE LastUpdated < DATEADD(HOUR, -1, GETDATE());\nIF (SELECT COUNT(*) FROM sys.dm_db_partition_stats ps\n    INNER JOIN sys.objects o ON ps.object_id = o.object_id\n    WHERE o.name = ''RollupData'' AND \n          ps.last_user_update < DATEADD(HOUR, -1, GETDATE())) > 0\nBEGIN\n    DROP TABLE RollupData;\nEND;';\n\n-- Schedule this script to run every hour using SQL Server Agent job or a similar scheduling tool.