-- Creating Table2 first because authorId is referenced from Table1\nCREATE TABLE Table2 (\n    authorId INT PRIMARY KEY,\n    authorName VARCHAR(255)\n);\n\n-- Creating Table1 and defining the foreign key relationship\nCREATE TABLE Table1 (\n    bookId INT PRIMARY KEY,\n    authorId INT,\n    bookName VARCHAR(255),\n    FOREIGN KEY (authorId) REFERENCES Table2(authorId)\n        ON DELETE RESTRICT  -- This will restrict deletion if the authorId is used in Table1\n        ON UPDATE CASCADE   -- This will update the authorId in Table1 if it changes in Table2\n);