CREATE PROCEDURE SelectDynamicFields\n    @selectField1 BIT,\n    @selectField2 BIT\nAS\nBEGIN\n    DECLARE @sql NVARCHAR(MAX);\n\n    -- Start the SELECT statement\n    SET @sql = 'SELECT ';\n\n    -- Conditionally add Field1 to the SELECT clause\n    IF @selectField1 = 1\n        SET @sql = @sql + 'Field1,';\n\n    -- Conditionally add Field2 to the SELECT clause\n    IF @selectField2 = 1\n        SET @sql = @sql + 'Field2,';\n\n    -- Remove the trailing comma if needed and add the FROM clause\n    SET @sql = LEFT(@sql, LEN(@sql) - 1);\n    SET @sql = @sql + ' FROM Table;';\n\n    -- Execute the dynamically constructed SQL statement\n    EXEC sp_executesql @sql;\nEND;