CREATE PROCEDURE UpdateMultipleTables\n    @new_value NVARCHAR(255),\n    @table1_id INT,\n    @table2_id INT,\n    @table3_id INT\nAS\nBEGIN\n    -- Start a transaction to ensure all updates are atomic\n    BEGIN TRANSACTION;\n\n    BEGIN TRY\n        UPDATE Table1 \n        SET field1 = @new_value \n        WHERE id = @table1_id;\n\n        UPDATE Table2 \n        SET field2 = @new_value \n        WHERE id = @table2_id;\n\n        UPDATE Table3 \n        SET field3 = @new_value \n        WHERE id = @table3_id;\n\n        -- If everything is successful, commit the transaction\n        COMMIT TRANSACTION;\n    END TRY\n    BEGIN CATCH\n        -- If an error occurs, roll back any changes made during the transaction\n        ROLLBACK TRANSACTION;\n        \n        -- Optionally, re-throw the caught exception to handle it at a higher level\n        THROW;\n    END CATCH\nEND;