DECLARE\n  TYPE t_customer_tab IS TABLE OF customer%ROWTYPE;\n  l_customers t_customer_tab := t_customer_tab();\nBEGIN\n  FOR i IN 1 .. 100000 LOOP\n    l_customers.EXTEND;\n    l_customers(l_customers.LAST).id := customer_seq.NEXTVAL;\n    l_customers(l_customers.LAST).business_name := dbms_random.STRING('A', 20);\n    -- Populate other columns similarly\n    l_customers(l_customers.LAST).email := 'asdfasf@asfasf.com';\n    \n    IF MOD(i, 1000) = 0 THEN\n      FORALL j IN 1 .. l_customers.COUNT SAVE EXCEPTIONS\n        INSERT INTO customer VALUES l_customers(j);\n      l_customers.DELETE;\n    END IF;\n  END LOOP;\n\n  -- Insert remaining customers if any\n  IF l_customers.COUNT > 0 THEN\n    FORALL j IN 1 .. l_customers.COUNT SAVE EXCEPTIONS\n      INSERT INTO customer VALUES l_customers(j);\n  END IF;\nEND;\n/