DECLARE @val AS FLOAT; -- Assuming the measurement column is of type FLOAT\n\nIF EXISTS (SELECT 1 FROM tbl_data WHERE persid = @curpersid AND yr = @curyear)\nBEGIN\n    SELECT @val = measurement \n    FROM tbl_data \n    WHERE persid = @curpersid AND yr = @curyear;\n\n    IF @val IS NULL\n        PRINT 'The value is NULL';\n    ELSE\n        PRINT 'A valid measurement was found: ' + CAST(@val AS VARCHAR);\nEND\nELSE\nBEGIN\n    PRINT 'No corresponding row at all';\nEND;