USE master;\nGO\n\nCREATE DATABASE CertificationDB;\nGO\n\nUSE CertificationDB;\nGO\n\nCREATE TABLE People (\n    PersonID INT PRIMARY KEY IDENTITY(1,1),\n    FirstName NVARCHAR(50) NOT NULL,\n    LastName NVARCHAR(50) NOT NULL\n);\n\nCREATE TABLE Certifications (\n    CertificationID INT PRIMARY KEY IDENTITY(1,1),\n    Title NVARCHAR(100) NOT NULL,\n    Description NVARCHAR(MAX)\n);\n\nCREATE TABLE PersonCertifications (\n    PersonCertificationID INT PRIMARY KEY IDENTITY(1,1),\n    PersonID INT FOREIGN KEY REFERENCES People(PersonID),\n    CertificationID INT FOREIGN KEY REFERENCES Certifications(CertificationID),\n    CertificatePath NVARCHAR(500) -- Path to the PDF file\n);