SELECT \n    a.AnimalID,\n    a.Name,\n    a.Category,\n    (s.date - ao.ReceivingDate) AS DaysToSell\nFROM \n    Sale s\nINNER JOIN \n    SaleAnimal sa ON s.SaleID = sa.SaleID\nINNER JOIN \n    Animal a ON sa.AnimalID = a.AnimalID\nINNER JOIN \n    AnimalOrderItem aio ON a.AnimalID = aio.AnimalID\nINNER JOIN \n    AnimalOrder ao ON aio.OrderID = ao.OrderID\nWHERE \n    a.Category = 'Cat'\nAND \n    (s.date - ao.ReceivingDate) > (\n        SELECT AVG(s.date - ao_ReceivingDate)\n        FROM Sale s\n        INNER JOIN SaleAnimal sa ON s.SaleID = sa.SaleID\n        INNER JOIN Animal a ON sa.AnimalID = a.AnimalID\n        INNER JOIN AnimalOrderItem aio ON a.AnimalID = aio.AnimalID\n        INNER JOIN AnimalOrder ao ON aio.OrderID = ao.OrderID\n        WHERE a.Category = 'Cat'\n    )\nORDER BY \n    DaysToSell DESC;