UPDATE c\nSET c.BusinessRegionId = b.Id\nFROM Company c\nINNER JOIN Locality l ON c.LocalityId = l.Id\nINNER JOIN \n(\n    SELECT Id\n    FROM BusinessRegion\n    WHERE tag = 'Australia/New South Wales'\n) b ON 1=1 -- This condition is just a placeholder\nWHERE l.StateOrProvinceAbbreviation = 'NSW'\nAND EXISTS (\n    SELECT 1\n    FROM BusinessRegion br\n    WHERE br.tag = 'Australia/New South Wales' AND br.Id = b.Id\n);