CREATE TABLE Student (\n        PersonID INT PRIMARY KEY,\n        StudentNumber VARCHAR(50),\n        EnrollmentDate DATE,\n        FOREIGN KEY (PersonID) REFERENCES Person(PersonID)\n    );\n\n    CREATE TABLE Employee (\n        PersonID INT PRIMARY KEY,\n        EmployeeNumber VARCHAR(50),\n        HireDate DATE,\n        FOREIGN KEY (PersonID) REFERENCES Person(PersonID)\n    );