SELECT \n    SQRT(\n        CASE \n            WHEN ((POWER(COALESCE(val1X, 0), 2) + POWER(COALESCE(val1Y, 0), 2)) > \n                  (POWER(COALESCE(val2X, 0), 2) + POWER(COALESCE(val2Y, 0), 2)))\n            THEN POWER(COALESCE(val1X, 0), 2) + POWER(COALESCE(val1Y, 0), 2)\n            ELSE POWER(COALESCE(val2X, 0), 2) + POWER(COALESCE(val2Y, 0), 2)\n        END\n    ) AS ResultVal\nFROM \n    myTable;