ALTER PROCEDURE [Admin].[STORED_PROC]\n    @SQL_STATEMENT NVARCHAR(MAX)\nAS\nBEGIN\n    -- Switch execution context to an account with sufficient privileges\n    EXECUTE AS USER = 'AdminUserWithSufficientPrivileges';\n\n    BEGIN TRY\n        EXEC (@SQL_STATEMENT);\n    END TRY\n    BEGIN CATCH\n        -- Logging or handling exceptions as needed\n        PRINT ERROR_MESSAGE();\n    END CATCH\n\n    -- Revert to the original execution context if necessary\n    REVERT;\nEND