-- Step 1: Create a snapshot table for Employee\nCREATE TABLE Employee_Snapshot AS (SELECT * FROM Employee WHERE 1=0);\n\n-- Step 2: Take current data as snapshot\nINSERT INTO Employee_Snapshot (SELECT * FROM Employee);\n\n-- (Perform your database changes here)\n\n-- Step 3: Query changes\nSELECT \n    'Inserted' AS ChangeType, t2.id, t2.name, t2.department \nFROM \n    Employee_Snapshot t1 \nRIGHT JOIN \n    Employee t2 \nON \n    t1.id = t2.id\nWHERE \n    t1.id IS NULL\n\nUNION ALL\n\nSELECT \n    'Deleted' AS ChangeType, t1.id, t1.name, t1.department \nFROM \n    Employee_Snapshot t1 \nLEFT JOIN \n    Employee t2 \nON \n    t1.id = t2.id\nWHERE \n    t2.id IS NULL\n\nUNION ALL\n\nSELECT \n    'Updated' AS ChangeType, t2.id, t2.name, t2.department \nFROM \n    Employee_Snapshot t1 \nINNER JOIN \n    Employee t2 \nON \n    t1.id = t2.id\nWHERE \n    NOT (t1.name = t2.name AND t1.department = t2.department);