-- Query for individual shifts\nSELECT \n    CONCAT(u.firstname, ' ', u.lastname) AS name, \n    u.id, \n    s.description, \n    s.shiftstart, \n    s.shiftend,\n    NULL AS total\nFROM shifts s\nINNER JOIN users u ON (s.userid = u.id)\nWHERE u.id = '$user'\nAND DATE(s.shiftstart) BETWEEN '$start' AND '$end'\nORDER BY s.shiftstart\n\nUNION ALL\n\n-- Query for the total sum\nSELECT \n    'Total:' AS name, \n    NULL AS id, \n    NULL AS description, \n    NULL AS shiftstart, \n    NULL AS shiftend,\n    SUM(TIME_TO_SEC(TIMEDIFF(shiftend, shiftstart)) / 3600) AS total\nFROM shifts s\nINNER JOIN users u ON (s.userid = u.id)\nWHERE u.id = '$user'\nAND DATE(s.shiftstart) BETWEEN '$start' AND '$end';