-- Creates a new SQL Server Agent Job to monitor long-running queries\n\nUSE msdb;\nGO\n\nEXEC sp_add_job @job_name = 'Monitor Long Running Queries';\n\nEXEC sp_add_jobstep \n    @job_name = 'Monitor Long Running Queries',\n    @step_name = 'Run Query for Long Running Procedures',\n    @subsystem = N'TSQL',\n    @command = N'\nUSE master;\nSELECT ... -- Use the above query here\n-- Additional logic to send notifications based on results';\n\nEXEC sp_add_schedule \n    @schedule_name = 'Run Every 5 Minutes', \n    @freq_type=4, \n    @freq_interval=1, \n    @freq_subday_type=2, \n    @active_start_time=0;\n\nEXEC sp_attach_schedule \n    @job_name='Monitor Long Running Queries', \n    @schedule_name='Run Every 5 Minutes';\n\nEXEC sp_add_jobserver \n    @job_name = 'Monitor Long Running Queries',\n    @server_name = N'(local)';