create table Entries (\n    Identifier int not null identity(1,1),\n    CompositionDate datetime not null default getdate(),\n    -- Create a calculated column to store the date as an integer (YYYYMMDD)\n    CompositionDateStamp as CAST(YEAR(CompositionDate) * 10000 + MONTH(CompositionDate) * 100 + DAY(CompositionDate) AS int) persisted,\n    Slug varchar(128) not null default '',\n    Title nvarchar(max) not null default N'',\n    ShortBody nvarchar(max) not null default N'',\n    Body nvarchar(max) not null default N'',\n    FeedbackState tinyint not null default 0,\n    constraint pk_Entries primary key clustered(Identifier),\n    -- Create a unique constraint on the date stamp and slug\n    constraint uk_Entries unique (CompositionDateStamp, Slug)\n);