CREATE TABLE waveforms (\n    id NUMBER PRIMARY KEY,\n    waveform_blob BLOB\n);\n\n-- Inserting a test blob (e.g., using UTL_RAW)\nDECLARE\n    l_waveform_floats FLOAT_ARRAY := FLOAT_ARRAY(1.0, 2.5, 3.6, ..., 9998.2); -- Replace with actual data\n    l_blob BLOB;\nBEGIN\n    DBMS_LOB.CREATETEMPORARY(l_blob, TRUE);\n    DBMS_LOB.CONVERTTOBLOB(l_blob, UTL_RAW.FROM_NUMBER_ARRAY(l_waveform_floats), DBMS_LOB.DEFAULT_CSID, 'AL32UTF8');\n    INSERT INTO waveforms (id, waveform_blob) VALUES (1, l_blob);\n    DBMS_LOB.FREETEMPORARY(l_blob);\nEND;\n/