DECLARE\n    l_blob BLOB;\n    l_float FLOAT;\nBEGIN\n    SELECT waveform_blob INTO l_blob FROM waveforms WHERE id = 1;\n\n    -- Assuming each float is stored in 8 bytes (binary_double)\n    IF DBMS_LOB.GETLENGTH(l_blob) >= 400 * 8 THEN\n        l_float := UTL_RAW.CAST_TO_BINARY_DOUBLE(DBMS_LOB.SUBSTR(l_blob, 8, 400 * 8 - 7));\n        DBMS_OUTPUT.PUT_LINE('400th number: ' || l_float);\n    ELSE\n        DBMS_OUTPUT.PUT_LINE('Not enough data in blob.');\n    END IF;\nEND;\n/