CREATE FUNCTION CONVERT_BIT_DATA_TO_INT(bit_data VARCHAR(4096) FOR BIT DATA, position INTEGER)\nRETURNS INT\nBEGIN ATOMIC\n  DECLARE first_byte INTEGER;\n  DECLARE second_byte INTEGER;\n  DECLARE combined_value INTEGER;\n\n  -- Extract the two bytes starting at the specified position\n  SET first_byte = ASCII(SUBSTR(bit_data, position * 2 - 1, 1));\n  SET second_byte = ASCII(SUBSTR(bit_data, position * 2, 1));\n\n  -- Combine the two bytes into a single integer\n  SET combined_value = (first_byte * 256) + second_byte;\n\n  RETURN combined_value;\nEND;