-- Assuming we have two tables: FactSales and DimProduct\n-- The DimProduct table has an integer primary key 'ProductID'\nCREATE TABLE DimProduct (\n    ProductID INT PRIMARY KEY,\n    ProductName NVARCHAR(100),\n    -- other product-related columns\n);\n\n-- The FactSales table references the ProductID in DimProduct\nCREATE TABLE FactSales (\n    SalesID INT PRIMARY KEY,\n    ProductID INT,\n    SaleDate DATE,\n    Amount DECIMAL(10, 2),\n    FOREIGN KEY (ProductID) REFERENCES DimProduct(ProductID)\n);