CREATE TABLE Comments (\n    ID INT NOT NULL PRIMARY KEY CLUSTERED,\n    Comment TEXT NOT NULL\n);\n\nGO\n\n-- Create a sequence starting from 1\nCREATE SEQUENCE dbo.CommentSequence\nAS INT\nSTART WITH 1\nINCREMENT BY 1;\n\nGO\n\n-- Create an INSTEAD OF INSERT trigger to adjust the ID values\nCREATE TRIGGER trg_Comments_Insert\nON Comments\nINSTEAD OF INSERT\nAS\nBEGIN\n    DECLARE @NextID INT;\n    SELECT @NextID = NEXT VALUE FOR dbo.CommentSequence;\n\n    WHILE (@NextID % 13 = 0)\n    BEGIN\n        -- Skip the number if it's a multiple of 13\n        SELECT @NextID = NEXT VALUE FOR dbo.CommentSequence;\n    END\n\n    INSERT INTO Comments (ID, Comment) VALUES (@NextID, (SELECT COALESCE(Comment, '') FROM inserted));\nEND;\n\nGO