-- To convert a timestamp with a specified offset to a timestamp with a named timezone:\nSELECT FROM_TZ(CAST(TIMESTAMP '2009-02-26 11:36:25.390713' AS TIMESTAMP), 'Pacific/Auckland') AS ts_with_named_tz FROM dual;\n\n-- To convert between time zones while preserving the named timezone:\nSELECT ts_with_named_tz AT TIME ZONE 'UTC' AS ts_in_utc FROM (\n    SELECT FROM_TZ(CAST(TIMESTAMP '2009-02-26 11:36:25.390713' AS TIMESTAMP), 'Pacific/Auckland') AS ts_with_named_tz FROM dual\n);