SELECT \n       -- some kind of information\n       d1d2.BusinessDayCount AS d1_d2_diff,\n       d1d3.BusinessDayCount AS d1_d3_diff,\n       d2d3.BusinessDayCount AS d2_d3_diff,\n       d2d4.BusinessDayCount AS d2_d4_diff\nFROM some_table st\nCROSS APPLY dbo.GetBusinessDaysBetween(st.DateOne, st.DateTwo) AS d1d2\nCROSS APPLY dbo.GetBusinessDaysBetween(st.DateOne, st.DateThree) AS d1d3\nCROSS APPLY dbo.GetBusinessDaysBetween(st.DateTwo, st.DateThree) AS d2d3\nCROSS APPLY dbo.GetBusinessDaysBetween(st.DateTwo, st.DateFour) AS d2d4;