-- Assume we have a table named 'people' with columns: favourite_colour, country, age_group\n\nSELECT \n    id,\n    favourite_colour,\n    country,\n    age_group,\n    -- Calculate similarity percentage\n    (CASE WHEN favourite_colour = 'Red' THEN 1 ELSE 0 END +\n     CASE WHEN country = 'United States' THEN 1 ELSE 0 END +\n     CASE WHEN age_group = '18-25' THEN 1 ELSE 0 END) / 3.0 * 100 AS similarity_percentage\nFROM \n    people\nORDER BY\n    similarity_percentage DESC;