INSERT INTO Customers (FirstName, LastName, Email, City) \nSELECT TOP 10000 -- Adjust this number for more data as needed\n    'FirstName' + RIGHT('00000'+CAST(RANK() OVER (ORDER BY t.name) AS NVARCHAR),5),\n    'LastName' + RIGHT('0000'+CAST(RANK() OVER (ORDER BY t.name) AS NVARCHAR),4),\n    'Email' + RIGHT('0000'+CAST(RANK() OVER (ORDER BY t.name) AS NVARCHAR),4) + '@example.com',\n    CASE WHEN RANK() OVER (ORDER BY t.name) % 10 = 0 THEN 'New York'\n         WHEN RANK() OVER (ORDER BY t.name) % 5 = 0 THEN 'Los Angeles'\n         ELSE 'Chicago' END\nFROM sys.all_objects AS T;