WITH Ranges AS (\n    SELECT \n        CASE \n            WHEN value BETWEEN 10 AND 20 THEN '10-20'\n            WHEN value BETWEEN 20.01 AND 32.5 THEN '20.01-32.5'\n            ELSE 'Other'\n        END AS ValueRange,\n        COUNT(*) AS RangeCount\n    FROM \n        YourTableName\n    GROUP BY \n        CASE \n            WHEN value BETWEEN 10 AND 20 THEN '10-20'\n            WHEN value BETWEEN 20.01 AND 32.5 THEN '20.01-32.5'\n        END\n),\nTotalCount AS (\n    SELECT COUNT(*) AS Total FROM YourTableName\n)\nSELECT \n    r.ValueRange,\n    r.RangeCount,\n    (r.RangeCount * 1.0 / t.Total) * 100 AS Percentage\nFROM \n    Ranges r, TotalCount t;