INSERT INTO Product (ProductNumber, TestResults, OtherColumns)\nSELECT \n    [ProductNumber] & 'B' & IIF([Count]>1, CStr([Count]), ''),\n    TestResults, \n    OtherColumns\nFROM (\n    SELECT \n        [ProductNumber], \n        TestResults, \n        OtherColumns,\n        (SELECT COUNT(*) + 1 FROM Product WHERE [ProductNumber] = '26273B' & ANY) AS [Count]\n    FROM \n        Product\n    WHERE \n        ProductNumber = '26273'\n);