DECLARE @SQL NVARCHAR(4000)\nDECLARE @TABLE_NAME SYSNAME\nDECLARE @COLUMN_NAME SYSNAME\nDECLARE @SEARCH_VALUE VARCHAR(100)\n\nSET @SEARCH_VALUE = '3' -- The value you are searching for\n\nDECLARE CURSOR_DB_CURSOR CURSOR FOR \n    SELECT TABLE_NAME, COLUMN_NAME\n    FROM INFORMATION_SCHEMA.COLUMNS\n    WHERE DATA_TYPE IN ('int', 'tinyint', 'smallint', 'bigint') -- Adjust data types as necessary\n    ORDER BY TABLE_NAME, COLUMN_NAME\n\nOPEN CURSOR_DB_CURSOR\n\nFETCH NEXT FROM CURSOR_DB_CURSOR INTO @TABLE_NAME, @COLUMN_NAME\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    SET @SQL = '\n        IF EXISTS (SELECT 1 FROM [' + @TABLE_NAME + '] WHERE [' + @COLUMN_NAME + '] = ' + QUOTENAME(@SEARCH_VALUE, '''') + ')\n            PRINT ''Found in table: [' + @TABLE_NAME + '] column: [' + @COLUMN_NAME + ']''\n    '\n\n    EXEC sp_executesql @SQL\n\n    FETCH NEXT FROM CURSOR_DB_CURSOR INTO @TABLE_NAME, @COLUMN_NAME\nEND \n\nCLOSE CURSOR_DB_CURSOR\nDEALLOCATE CURSOR_DB_CURSOR