-- Example of multiple smaller batch updates in SQL Server\nDECLARE @TransactionBatchSize INT = 1000; -- Adjust this value as necessary\n\nWHILE (SELECT COUNT(*) FROM YourTargetTable WHERE UpdateNeeded = 1) > 0\nBEGIN\n    BEGIN TRANSACTION;\n\n    UPDATE TOP (@TransactionBatchSize) tt\n    SET Column1 = st.Column1,\n        Column2 = st.Column2\n    FROM YourTargetTable tt\n    JOIN SourceTable st ON tt.PrimaryKeyColumn = st.PrimaryKeyColumn\n    WHERE tt.UpdateNeeded = 1;\n\n    COMMIT TRANSACTION;\nEND;