DECLARE @BatchSize INT = 100;\nDECLARE @Offset INT = 0;\n\nWHILE (SELECT COUNT(*) FROM TBL) > 0\nBEGIN\n    BEGIN TRANSACTION;\n    \n    -- Delete entries from TBL in batches\n    DELETE TOP (@BatchSize) FROM TBL\n    WHERE ID IN (\n        SELECT TOP (@BatchSize + @Offset) ID \n        FROM TBL WITH (READPAST)\n    );\n    \n    COMMIT TRANSACTION;\n\n    SET @Offset = @Offset + @BatchSize;\nEND