declare @RowIdMin int = 10;\ndeclare @RowIdMax int = 25;\ndeclare @ColSort varchar(100) = 'MyColumn'; -- Change this to the column you want to sort by\ndeclare @SQL nvarchar(max);\n\nset @SQL = N'\nselect * \nfrom (\n    select Col1, Col2, ROW_NUMBER() over (order by ' + QUOTENAME(@ColSort) + ' desc) as RowId  \n    from MyTable\n) dt  \nwhere RowId BETWEEN @RowIdMin AND @RowIdMax';\n\nexec sp_executesql @SQL, N'@RowIdMin int, @RowIdMax int', @RowIdMin, @RowIdMax;