CREATE TABLE InvoiceNumbers (\n    Id INT IDENTITY(1,1) PRIMARY KEY,\n    InvoiceNumber NVARCHAR(50) UNIQUE NOT NULL\n);\n\nGO\n\nCREATE PROCEDURE GenerateInvoiceNumber\nAS\nBEGIN\n    DECLARE @NewInvoiceNumber NVARCHAR(50);\n    SET @NewInvoiceNumber = NEWID(); -- Generates a unique alphanumeric string\n\n    INSERT INTO InvoiceNumbers (InvoiceNumber) VALUES (@NewInvoiceNumber);\n    SELECT @NewInvoiceNumber AS NewInvoiceNumber;\nEND\nGO