WITH \n    StepOne AS (\n        SELECT /*+ NOPARALLEL bypass_recursive_check */ SP_ALIAS_190, SP_ALIAS_191\n        FROM YourTable\n        WHERE SomeCondition\n    ),\n    StepTwo AS (\n        SELECT SP_ALIAS_190,\n               CASE SP_ALIAS_191\n                   WHEN 1 THEN 'PROVIDER::ALL_PROV::'\n                   WHEN 0 THEN 'OTHER_VALUE'\n               END AS derived_column\n        FROM StepOne\n    )\nSELECT * FROM StepTwo;