WITH RECURSIVE OrderedLinkedList AS (\n    -- Anchor member: select the head of the list (where ParentId is NULL)\n    SELECT Id, ParentId, SomeData, CAST(SomeData AS NVARCHAR(MAX)) AS SortPath\n    FROM LinkedList\n    WHERE ParentId IS NULL\n\n    UNION ALL\n\n    -- Recursive member: join to get the next node in the list\n    SELECT ll.Id, ll.ParentId, ll.SomeData, oll.SortPath + '/' + ll.SomeData\n    FROM LinkedList ll\n    INNER JOIN OrderedLinkedList oll ON ll.ParentId = oll.Id\n)\nSELECT Id, ParentId, SomeData\nFROM OrderedLinkedList\nORDER BY SortPath;