SELECT \n    GROUP_ID,\n    RTRIM(XMLAGG(XMLELEMENT(E, VALUE || ',')).EXTRACT('//text()'), ',') AS GroupValues\nFROM (\n    WITH RankedData AS (\n        SELECT \n            GROUP_ID,\n            ID,\n            VALUE,\n            ROW_NUMBER() OVER (PARTITION BY GROUP_ID ORDER BY ID) - 1 AS ParentID\n        FROM \n            YourTable\n    )\n    SELECT \n        RD.GROUP_ID,\n        RD.VALUE\n    FROM \n        RankedData RD\n    START WITH ParentID = 0\n    CONNECT BY PRIOR ID = ParentID AND PRIOR GROUP_ID = GROUP_ID\n)\nGROUP BY GROUP_ID;