CREATE TRIGGER AfterUpdate_Trigger_YourTableName\n   ON YourTableName\n   AFTER UPDATE\n   AS\n   BEGIN\n       SET NOCOUNT ON;\n\n       INSERT INTO AuditLog (TableName, RecordID, FieldName, OldValue, NewValue, ChangedBy)\n       SELECT 'YourTableName', i.RecordID, Column_Name,\n              ISNULL(CONVERT(NVARCHAR(MAX), d.[Column_Name]), 'Null'),\n              CONVERT(NVARCHAR(MAX), i.[Column_Name]),\n              SYSTEM_USER\n       FROM inserted i\n       INNER JOIN deleted d ON i.RecordID = d.RecordID\n       UNPIVOT (\n           [Value] FOR Column_Name IN ([Field1], [Field2], ...) -- List all fields here\n       ) unpvt\n       WHERE ISNULL(i.[Column_Name], '') <> ISNULL(d.[Column_Name], '');\n\n       -- Update Modified Fields JSON column in YourTableName\n       UPDATE t\n       SET ModifiedFields = JSON_MODIFY(\n           COALESCE(t.ModifiedFields, '[]'),\n           '$.' + Column_Name,\n           (SELECT TOP 1 ChangeDate FROM AuditLog WHERE TableName = 'YourTableName' AND RecordID = i.RecordID AND FieldName = Column_Name ORDER BY ChangeDate DESC)\n       )\n       FROM inserted i\n       INNER JOIN YourTableName t ON i.RecordID = t.RecordID\n       UNPIVOT (\n           [Value] FOR Column_Name IN ([Field1], [Field2], ...) -- List all fields here\n       ) unpvt;\n   END;