DECLARE @SearchQuery AS NVARCHAR(100) = 'yellow bird'\nDECLARE @SQL AS NVARCHAR(MAX)\nDECLARE @Word AS NVARCHAR(50)\nDECLARE @Words TABLE (Value NVARCHAR(50))\nDECLARE @Counter INT = 1\n\n-- Split the search query into individual words and insert them into a table\nWHILE CHARINDEX(' ', @SearchQuery) > 0\nBEGIN\n    SET @Word = LEFT(@SearchQuery, CHARINDEX(' ', @SearchQuery) - 1)\n    INSERT INTO @Words (Value) VALUES (@Word)\n    SET @SearchQuery = SUBSTRING(@SearchQuery, CHARINDEX(' ', @SearchQuery) + 1, LEN(@SearchQuery))\nEND\n\n-- Insert the last word, if any\nIF LEN(@SearchQuery) > 0\n    INSERT INTO @Words (Value) VALUES (@SearchQuery)\n\n-- Construct the SQL query dynamically\nSELECT @SQL = 'SELECT Id, Name FROM dbo.FooBars WHERE '\nSELECT @SQL += 'CONTAINS(Name, N''FORMSOF(Thesaurus, ' + Value + '')'') '\nFROM @Words\nWHERE Counter = 1\n\nIF (SELECT COUNT(*) FROM @Words) > 1\nBEGIN\n    DECLARE @Index INT = 2\n    WHILE @Index <= (SELECT COUNT(*) FROM @Words)\n    BEGIN\n        SELECT @SQL += 'AND CONTAINS(Name, N''FORMSOF(Thesaurus, ' + Value + '')'') '\n        FROM @Words WHERE Counter = @Index\n        SET @Index = @Index + 1\n    END\nEND\n\n-- Execute the dynamically constructed SQL query\nEXEC sp_executesql @SQL