SELECT name, city\nFROM users\nWHERE latitude BETWEEN (? - {radius_latitude}) AND (? + {radius_latitude})\nAND longitude BETWEEN (? - {radius_longitude}) AND (? + {radius_longitude})\nAND (69.1 * (latitude - ?)) * (69.1 * (latitude - ?))\n+ ((69.1 * (longitude - ?) * COS(? / 57.3)) * (69.1 * (longitude - ?) * COS(? / 57.3))) < {radius_squared}\nORDER BY \n    (69.1 * (latitude - ?)) * (69.1 * (latitude - ?))\n+ ((69.1 * (longitude - ?) * COS(? / 57.3)) * (69.1 * (longitude - ?) * COS(? / 57.3))) ASC;