CREATE VIEW V_TreasuryYieldCurveInterpolated AS\nSELECT Year,\n       CASE \n           WHEN Year IN (4, 6, 8, 9, 11, 12, 13, 14, 15, 16, 17, 18, 19, 21, 22, 23, 24, 25, 26, 27, 28, 29)\n           THEN ((SELECT YieldValue FROM TreasuryYieldCurve WHERE Year = Year - 1) + \n                 (SELECT YieldValue FROM TreasuryYieldCurve WHERE Year = Year + 1)) / 2.0\n           ELSE YieldValue\n       END AS InterpolatedYieldValue\nFROM (\n    SELECT DISTINCT Year FROM TreasuryYieldCurve\n    UNION ALL\n    SELECT * FROM (VALUES(4), (6), (8), (9), (11), (12), (13), (14), (15), (16), (17), (18), (19),\n                         (21), (22), (23), (24), (25), (26), (27), (28), (29)) AS missingYears(year)\n) AS FullYearList\nLEFT JOIN TreasuryYieldCurve ON FullYearList.Year = TreasuryYieldCurve.Year;