WITH CombinedData AS (\n    SELECT category, row_data, value FROM table1\n    UNION ALL\n    SELECT category, row_data, value FROM table2\n    UNION ALL\n    SELECT category, row_data, value FROM table3\n)\nSELECT \n    category,\n    MAX(CASE WHEN row_data = 'Column1' THEN value END) AS Column1_Value,\n    MAX(CASE WHEN row_data = 'Column2' THEN value END) AS Column2_Value,\n    MAX(CASE WHEN row_data = 'Column3' THEN value END) AS Column3_Value\nFROM \n    CombinedData\nGROUP BY \n    category;