CREATE PROCEDURE InsertParentAndChild\n    @Timestamp DATETIME -- Assuming this is the parameter you want to pass to insert into the parent table\nAS\nBEGIN\n    DECLARE @NewID INT; -- Variable to hold the auto-incremented ID from the parent table\n\n    -- Insert into Parent Table and retrieve the new ID\n    INSERT INTO PARENT_TABLE (TIMESTAMP_COLUMN) VALUES (@Timestamp);\n    SET @NewID = SCOPE_IDENTITY();\n\n    -- Use the retrieved ID to insert into Child Table\n    INSERT INTO CHILD_TABLE (PARENT_ID, OTHER_COLUMNS)\n    VALUES (@NewID, 'Your other values here');\nEND;