-- Insert a new customer and capture the generated CustomerID\nINSERT INTO Customers (CustomerName, ContactEmail)\nVALUES ('John Doe', 'john.doe@example.com');\n\nDECLARE @InsertedCustomerId INT;\nSET @InsertedCustomerId = SCOPE_IDENTITY();\n\n-- Use the captured CustomerID to insert a new order\nINSERT INTO Orders (OrderDate, CustomerId, TotalAmount)\nVALUES (GETDATE(), @InsertedCustomerId, 299.99);