BEGIN;\n\n-- Use temporary IDs or values to hold the intermediate state\nUPDATE PREFERENCES\nSET fk_idstructure = CASE \n                        WHEN idpref = 1 THEN 2000 -- Temporary placeholder\n                        ELSE fk_idstructure \n                     END\nWHERE idpref IN (1, 2);\n\nUPDATE PREFERENCES\nSET fk_idstructure = CASE \n                        WHEN idpref = 2 THEN 1\n                        WHEN idpref = 1 THEN 2\n                     END\nWHERE idpref IN (1, 2);\n\nCOMMIT;